%Convert USC-HAD data from .mat to .csvprocessed_filepath = '/Users/joshcherian/Documents/GitHub/Activity-Transition-Detection/Data/phase_1/processed/USC-HAD/';cd '/Users/joshcherian/Documents/GitHub/Activity-Transition-Detection/Data/phase_1/raw/USC-HAD/'usc_dir = dir;usc_dir = usc_dir(~ismember({usc_dir.name}, {'.', '..'}));usc_dir( ~[usc_dir.isdir] ) = [];for subject = 1:numel(usc_dir)  curr_usc_dir = dir(strcat(usc_dir(subject).folder, '/', usc_dir(subject).name));  currSubject = usc_dir(subject).name  for mat_file = 1:numel(curr_usc_dir)    currmat = strcat(currSubject, '/', curr_usc_dir(mat_file).name);    if regexp(currmat, '\.mat', 'match', 'once')      load(currmat)      if ~exist(strcat(processed_filepath, currSubject), 'dir')        mkdir(strcat(processed_filepath, currSubject));      end      fid = fopen(strcat(processed_filepath, currSubject, '/', curr_usc_dir(mat_file).name(1:end-4), '.csv'), 'wt');      fprintf(fid, '%s,%s,%s,%s,%s,%s,%s\n', 'AccX', 'AccY', 'AccZ', 'GyroX', 'GyroY', 'GyroZ','Activity');            cell_data = num2cell(sensor_readings');      cell_data = [cell_data' repmat({activity},length(sensor_readings),1)];      cell_data_T = cell_data';      fprintf(fid, '%g,%g,%g,%g,%g,%g,%s\n', cell_data_T{:})      cell_data = {};      fclose(fid);    end  endendcd ..